import json
from flask import Flask, request, jsonify

app = Flask(__name__)

# In a real application, this would be a secure database or a file
user_data = {}

@app.route('/register', methods=['POST'])
def register():
    data = request.get_json()
    if not data or 'email' not in data or 'consent' not in data:
        return jsonify({"error": "Missing required fields"}), 400

    email = data['email']
    consent = data['consent']

    if not consent:
        return jsonify({"error": "User must give consent to process their data"}), 400

    user_data[email] = {
        'email': email,
        'consent': consent
    }

    return jsonify({"message": "User registered successfully"}), 201

@app.route('/data', methods=['GET'])
def get_data():
    if not request.args.get('email'):
        return jsonify({"error": "Missing email parameter"}), 400

    email = request.args.get('email')
    if email not in user_data:
        return jsonify({"error": "User not found"}), 404

    return jsonify(user_data[email]), 200

@app.route('/data', methods=['DELETE'])
def delete_data():
    if not request.args.get('email'):
        return jsonify({"error": "Missing email parameter"}), 400

    email = request.args.get('email')
    if email not in user_data:
        return jsonify({"error": "User not found"}), 404

    del user_data[email]
    return jsonify({"message": "User data deleted successfully"}), 200

if __name__ == '__main__':
